import { useFormik } from "formik";
import React, { useRef } from "react";
import { useForm } from "react-hook-form";
import './inscription.css';

const FieldRadio=React.forwardRef(function(props,ref) {
    const { name, value, onChange, children } = props

    return (
        <div className="form-outline mb-4 ">

            <label  >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <div >
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio1" className="form-check-input" />
                    <label className="custom-control-label" for="radio1">Masculin</label>
                </div>
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio2" className="form-check-input" />
                    <label className="custom-control-label" for="radio2">Feminin</label>
                </div>
            </div>
        </div>
    )
})

const Field=React.forwardRef(function (props,ref) {
    const { type, name, value, onChange, children } = props
    return (
        <div className="form-outline mb-4">
            <label htmlFor={name}>{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs  */}
            <input type={type} name={name} value={value} onChange={onChange} id={name} ref={ref} className="form-control form-control-lg" />

        </div>
    )
})

const validate = values => {
    const errors = {};
  
    if (!values.nom) {
      errors.nom = 'Required';
    } else if (values.nom.length > 15) {
      errors.nom = 'Le nom doit être 15 caractères ou moins ';
    }
  
    if (!values.prenom) {
      errors.lastName = 'Required';
    } else if (values.prenom.length > 20) {
      errors.prenom = 'Le prenom doit être 20 caractères ou moins';
    }
 
    if (!values.username) {
     errors.username = 'Required';
   } else if (values.username.length > 10) {
     errors.username = 'Le prenom doit être 10 caractères ou moins';
   }
  
    if (!values.email) {
      errors.email = 'Required';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Adresse email invalide';
    }
 
    if(!values.sexe){
     errors.sexe = 'Required';
    }
 
    if(!values.naissance){
     errors.naissance='Required';
    }
    
    if (!values.lieu) {
     errors.naissance = 'Required';
   } else if (values.naissance.length > 20) {
     errors.naissance = 'Le prenom doit être 20 caractères ou moins';
   }
   if (!values.nationalite) {
     errors.nationalite = 'Required';
   } else if (values.nationalite.length > 10) {
     errors.nationalite = 'Le prenom doit être 10 caractères ou moins';
   }
 
   const passwordRegex = /(?=.*[0-9])/;
   if(!values.password1){
     errors.password1='Required';
   }else if (values.length < 8) {
     errors.password1= "*Le mot de passe doit contenir au moins 8 caractères.";
   } else if (!passwordRegex.test(values)) {
     errors.password1 = "Mot de passe invalide.Doit contenir au moins un chiffre";
   }
 
 
 
    return errors;
  };
  

// {/* // Créer le composant Inscription */}

function Modification(){ 
    const {register,handleSubmit,formState:{errors}}=useForm(); //register est une fonction qui permet d'enregistrer les valeurs entrées dans les champs
    
    const handleErrors = (errors) ={

    }
     
    const ErrorOptions = {
        nom:{required:'Veuillez entrer votre nom!'},
        prenom:{required:'Veuillez entrer votre prenom!'},
        username:{required:"Veuillez entrer votre nom d'utilisateur!"},
        sexe:{required:'Veuillez choisir le sexe'},
        email:{required:'Veuillez entrer votre email!',
               pattern:{
                value:/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                
               }},
        telephone:{required:'Veuillez entrer votre numéro de téléphone!'},
        naissance:{required:'Veuillez entrer votre date de naissance!'},
        lieu:{required:'Veuillez entrer votre lieu de naissance!'},
        password:{required:'Mot de passe'},
        confirmPassword:{required:'Confirmezlz mot de passe'}
    }

    const myRef=useRef(null);
    const partie1=useRef(null);
    const partie2=useRef(null);
    const suivant=useRef(null);
    const precedent=useRef(null);
    const refNom=useRef(null);
    const refPrenom=useRef(null);
    const refUsername=useRef(null);
    const refEmail=useRef(null);
    const refSexe=useRef(null);
    const refNaissance=useRef(null);
    const refLieu=useRef(null);
    const refTelephone=useRef(null);
    const refNationalite=useRef(null);
    const refPassword1=useRef(null);
    const refPassword2=useRef(null);

    // code.current="text-center  taille_image";

   const handleClick1 = () => {
        suivant.current.classList.add('d-none')
        precedent.current.classList.remove('d-none');
        if (partie2.current.classList.contains('toggleForm')) {
            myRef.current.style.transform = 'translateY(-50%)';
            myRef.current.style.transition = 'transform .5s';
            partie2.current.classList.remove('toggleForm');
            partie1.current.classList.add('toggleForm')
        }
    }

  const  handleClick2 = () => {
        precedent.current.classList.add('d-none')
        suivant.current.classList.remove('d-none');
        if (partie1.current.classList.contains('toggleForm')) {
            myRef.current.style.transform = 'translateY(0%)';
            myRef.current.style.transition = 'transform .5s';
            partie2.current.classList.add('toggleForm')
            partie1.current.classList.remove('toggleForm');
        }
    }

 const handleClick3 = (e) => {
//  setNom("");

// setFormData({
//     nom:"",
//     prenom:""
// })
refNom.current="";
 refPrenom.current="";
 refUsername.current="";
 refEmail.current="";
 refSexe.current="";
 refNaissance.current="";
 refLieu.current=""; 
 refTelephone.current="";
 refNationalite.current="";
 refPassword1.current="";
 refPassword2.current="";

    
precedent.current.classList.add('d-none')
suivant.current.classList.remove('d-none');
if (partie1.current.classList.contains('toggleForm')) {
    myRef.current.style.transform = 'translateY(0%)';
    myRef.current.style.transition = 'transform .5s';
    partie2.current.classList.add('toggleForm')
    partie1.current.classList.remove('toggleForm');
 }
       
    }

    const formik=useFormik({
        initialValues: {
            nom: '',
            prenom:'',
            username: '',
            email:'',
            sexe: '',
            naissance: '',
            lieu: '',
            telephone: '',
            nationalite: '',
            password1: '',
            password2:'' ,},
            validate,
            onSubmit: values => {
              alert(JSON.stringify(values, null, 2));
            },
    })

    const onSubmit = data => {
        console.log(data)
    }
        return (
            <div className="back">
                <img src="logo_sansfond.png" alt="logo gnb" className="text-center  taille_image" />
                <section className="vh-100 " >
                    <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>

                    <div className="container py-5 h-100">
                        <div className="row d-flex justify-content-center align-items-center h-100">
                            <div className="col col-xl-10">
                                <div className="card bords" style={{ overflow: "hidden", }}>
                                    <div className="row g-0" >
                                        <div className="col-md-6 col-lg-5 d-none d-md-block">
                                            <img src="image3.jpg " alt="login form" className="img-fluid image" />

                                        </div>

                                        <div className="col-md-6 col-lg-7 d-flex align-items-center">

                                            <div className="card-body p-4 ps-lg-5 pe-lg-5 pt-lg-3 text-black" id="formContainer" ref={myRef}>

                                                <form  onSubmit={handleSubmit(onSubmit,handleErrors)} >
                                                    <div className="d-flex align-items-center mb-3 pb-1"></div>

                                                    <div id="formPartie1" className="" ref={partie1}>

                                                        <Field type='text' name='nom' ref={register('nom',ErrorOptions.nom)}>Nom </Field>
                                                        <small className="text-danger">{errors?.nom && errors.nom.message}</small>        

                                                        <Field type='text' name='prenom'   ref={register('prenom',ErrorOptions.prenom)} >Prénom </Field>
                                                        <small className="text-danger">{errors?.prenom && errors.prenom.message}</small>        
        
                                                        <Field type="text" name='username'  ref={register('username',ErrorOptions.username)}>Username</Field>
                                                        <small className="text-danger">{errors?.username && errors.username.message}</small>        
                                                        
                                                        <FieldRadio name='sexe'  ref={register('sexe',ErrorOptions.sexe)}>Sexe</FieldRadio>
                                                        <small className="text-danger">{errors?.sexe && errors.sexe.message}</small>        
  
                                                        <Field type="email" name='email' ref={register('email',ErrorOptions.email)}>Email</Field>
                                                        <small className="text-danger">{errors?.email && errors.email.message}</small>        
                
                                                        <Field type="tel" name='telephone'  ref={register('telephone',ErrorOptions.telephone)}>Numéro de téléphone</Field>
                                                        <small className="text-danger">{errors?.telephone && errors.telephone.message}</small>        
                
                                                    </div>
                                                    {/* <div className="pt-1 mb-4"></div> */}
                                                    <div className="pt-1 mb-4 col-md-2 offset-md-10">
                                                        <div id="suivant" className="btn btn-sm btn-secondary text-white" onClick={handleClick1} ref={suivant} type="button"><i className="me-2 fa-solid fa-circle-chevron-down "></i>Suivant</div>
                                                        {/* <Link to='/'>Retour<i className="fa-solid fa-circle-chevron-right "></i></Link> */}
                                                    </div>

                                                    <div id="formPartie2" className="toggleForm" ref={partie2}>

                                                        <Field type="date" name='naissance'  ref={register('naissance',ErrorOptions.naissance)}>Date de naissance</Field>
                                                        <small className="text-danger">{errors?.naissance && errors.naissance.message}</small>        
                
                                                        <Field type="text" name='lieu'   ref={register('lieu',ErrorOptions.lieu)}>Lieu de naissance</Field>
                                                        <small className="text-danger">{errors?.lieu && errors.lieu.message}</small>        
                              
                                                        <Field type="text" name='nationalite'   ref={register('nationalite',ErrorOptions.nationalite)}>Nationalité</Field>
                                                        <small className="text-danger">{errors?.nationalite && errors.nationalite.message}</small>        
                
                                                        <Field type="password" name='password'   ref={register('password',ErrorOptions.password)}>Mot de passe</Field>
                                                        <small className="text-danger">{errors?.password && errors.password.message}</small>        
                
                                                        <Field type="password" name='confirmPassword'   ref={register('confirmPassword',ErrorOptions.confirmPassword)}>Confirmer le mot de passe</Field>
                                                        <small className="text-danger">{errors?.confirmPassword && errors.confirmPassword.message}</small>        
                                                                
                                                        <div className="row  d-none" ref={precedent}>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="precedent" className="btn btn-sm btn-secondary text-white" onClick={handleClick2}><i className="me-2 fa-solid fa-circle-chevron-up "></i>Précédent</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4">
                                                                <div id="annuler" className="btn btn-sm btn-danger text-white" onClick={handleClick3}><i className="me-2 fa-solid fa-circle-xmark "></i>Annuler</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="inscrire" className="btn btn-sm btn-primary text-white"><i className="me-2 fa-solid fa-circle-user"></i>S'inscrire</div>
                                                            </div>
                                                        </div>

                                                    </div>

                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
}

export default Modification
