import React from "react";
// import { NavLink,Link } from "react-router-dom";
// import { Nav} from "react-bootstrap";
import Headers from '../Header/header';
import Profils from "../Profils/profils";
import Services from "../Services/services";
import './navbar.css'
// import './scripts.js';


function Scroll() {
    
        window.addEventListener('DOMContentLoaded', event =>   {                      //Le DOMContentLoaded est déclenché quand le document HTML initial est complètement chargé et analysé, sans attendre la fin du chargement des feuilles destyles,images et sous-document. 

            // Navbar shrink function
            var navbarShrink = function () {
                const navbarCollapsible = document.body.querySelector('#mainNav');
                if (!navbarCollapsible) {
                    return;
                }
                if (window.scrollY === 0) {
                    navbarCollapsible.classList.remove('navbar-shrink')
                } else {
                    navbarCollapsible.classList.add('navbar-shrink')
                }
        
            };
        
            // Shrink the navbar 
            navbarShrink();
        
            // Shrink the navbar when page is scrolled
            document.addEventListener('scroll', navbarShrink);
        
            // Activate Bootstrap scrollspy on the main nav element
            const mainNav = document.body.querySelector('#mainNav');
            if (mainNav) {
             this.props.bootstrap.ScrollSpy(document.body, {
                    target: '#mainNav',
                    offset: 74,
                });
            };
        
            // Collapse responsive navbar when toggler is visible
            const navbarToggler = document.body.querySelector('.navbar-toggler');
            const responsiveNavItems = [].slice.call(
                document.querySelectorAll('#navbarResponsive .nav-link')
            );
            responsiveNavItems.map( (responsiveNavItem)=> 
                responsiveNavItem.addEventListener('click', () => {
                    if (window.getComputedStyle(navbarToggler).display !== 'none') {
                        navbarToggler.click();
                    }
                })
            );
        
        })
    
}


export default class Navbar extends React.Component{
    render(){
       
    }
}
