import React,{useState} from "react";
import './inscription.css';
import { useNavigate } from "react-router-dom";
import { Validator } from "react";


function Fieldtext(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='text' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fieldmail(props){
    const {name,value,onChange,children}=props
    const [emailError , setEmailError] = useState('');
    const handleChangeEmail = (event) =>{
        if (Validator.isEmail(email)) {
            setEmailError('Email valide :)')
        } else {
          setEmailError('Entrer un email valide! ')  
        }
    }
            
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='email' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}


function Fieldselect(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
 
          <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
          <select value={value} onChange={onChange} name={name}>
            <option value='homme'>Homme</option>
            <option value='femme'>Femme</option>
          </select>
        </div>
    )
}

function Fielddate(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='date' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fieldnumber(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='tel' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

 function Fieldpass(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
 
          <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='password' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

// {/* // Créer le composant Inscription */}

export default class Inscription extends React.Component{


 //Créer un constructeur qui prendra en paramètre les propriétés
    constructor(props){
        super(props)
        this.state={
            nom:'',
            prenom:'',
            username:'',
            email:'',
            sexe:'',
            naissance:'',
            lieu:'',
            telephone:'',
            nationalite:'',
            password1:'',
            password2:''
        }
this.handleChange=this.handleChange.bind(this)
this.handleSubmit=this.handleSubmit.bind(this)
    }

    

// {/* l'évènement onChange fait appel à la fonction handleChange*/}     

handleChange(event){   
    const name=event.target.name
    // const type=event.target.type  
    // const value=   type == 'checkbox' ? event.target.checked : event.target.value          
        this.setState({                    //*Modifier la valeur du champ */}
            [name]: event.target.value
        })
    }

handleChangeEmail(){
    const email = event.target.value

}

 handleClick = () =>{
        const navigate = useNavigate()
          navigate(-1)
    }

 handleSubmit = (event) =>{
    // const password1=event.target.password1
    // const password2=event.target.password2
const password1=this.state.password.slice();
const password2=this.state.password2.slice();

    if(password1!==password2){
        alert('Les mots de passe ne sont pas identiques!')
    }else{
        alert('Formulaire soumis avec succès')
    }
    event.preventDefault()
 }   

// 

    render(){
        return(
            <div className="back">
            <img src="logo_sansfond.png" alt="logo gnb" className="text-center  taille_image"/> 
            <section className="vh-100 " >
            <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>

                <div className="container py-5 h-100">
                    <div className="row d-flex justify-content-center align-items-center h-100">
                        <div className="col col-xl-10">
                            <div className="card bord" >
                                <div className="row g-0">
                                    <div className="col-md-6 col-lg-5 d-none d-md-block">
                                        <img src="image3.jpg"
                                        alt="login form" className="img-fluid image"  />

                                    </div>
                                    
                                    <div className="col-md-6 col-lg-7 d-flex align-items-center">
                                        <div className="card-body p-4 p-lg-5 text-black">
            
                                            <form onSubmit={this.handleSubmit}>

                                                <div className="d-flex align-items-center mb-3 pb-1">
                                                    {/* <span className="h1 fw-bold mb-0">Global Network Business</span> */}
                                                </div>


                                                <Fieldtext name='nom' value={this.state.nom} onChange={this.handleChange} >Nom</Fieldtext>
                                                <Fieldtext name='prenom' value={this.state.prenom} onChange={this.handleChange}>Prénom</Fieldtext>
                                                <Fieldtext name='username' value={this.state.username} onChange={this.handleChange}>Pseudo</Fieldtext>
                                                <Fieldmail name='email' value={this.state.email} onChange={this.handleChange}>Email</Fieldmail><br/>
                                                <span className="message">{emailError}</span>
                                                <Fieldselect name='sexe' value={this.state.sexe} onChange={this.handleChange}>Sexe</Fieldselect>
                                                <Fielddate name='prenom' value={this.state.prenom} onChange={this.handleChange}>Prénom</Fielddate>
                                                <Fieldtext name='lieu' value={this.state.lieu} onChange={this.handleChange}>Lieu de naissance</Fieldtext>
                                                <Fieldnumber name='telephone' value={this.state.telephone} onChange={this.handleChange}>Numéro de téléphone</Fieldnumber>
                                                <Fieldtext name='nationalite' value={this.state.nationalite} onChange={this.handleChange}>Nationalité</Fieldtext>
                                                <Fieldpass name='password' value={this.state.password1} onChange={this.handleChange}>Mot de passe</Fieldpass>
                                                <Fieldpass name='password' value={this.state.password2} onChange={this.handleChange}>Confirmer le mot de passe</Fieldpass>


                                                <div className="col md-3">
                                                    <div className="pt-1 mb-4">
                                                    <button className="btn btn-lg btn-block fond_bleu text-white" type="submit" value="Submit" >S'inscrire</button>
                                                    </div>
                                                </div>
                                                <div className="col md-6"></div>
                                                <div className="col md-3">
                                                <button onClick={this.handleClick} className='boutons'>Retour<i className="fa-solid fa-circle-chevron-right "></i></button>
                                                </div>
                                                
                                            </form>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
            </div>
        )
    }
}
