import React from "react";
import './inscription.css';
// import {useEffect} from 'react';
// import { useNavigate } from "react-router-dom";

function FieldRadio(props) {
    const { name, value, onChange, children } = props

    return (
        <div className="form-outline mb-4 ">

            <label  >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <div >
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio1" className="form-check-input" />
                    <label className="custom-control-label" for="radio1">Masculin</label>
                </div>
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio2" className="form-check-input" />
                    <label className="custom-control-label" for="radio2">Feminin</label>
                </div>
            </div>
        </div>
    )
}

function Field(props) {
    const { type, name, value, onChange, children } = props
    return (
        <div className="form-outline mb-4">
            <label htmlFor={name}>{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs  */}
            <input type={type} name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg" />

        </div>
    )
}

// {/* // Créer le composant Inscription */}

export default class Inscription extends React.Component {


    //Créer un constructeur qui prendra en paramètre les propriétés
    constructor(props) {
        super(props);
        this.code = "text-center  taille_image";
        this.refNom=React.createRef();
        this.refPrenom=React.createRef();
        this.refUsername=React.createRef();
        this.refSexe=React.createRef();
        this.refEmail=React.createRef();
        this.refTelephone=React.createRef();
        this.refNaissance=React.createRef();
        this.refLieu=React.createRef();
        this.refNationalite=React.createRef();
        this.refPassword1=React.createRef();

        this.myRef = React.createRef();
        this.partie1 = React.createRef();
        this.partie2 = React.createRef();
        this.suivant = React.createRef();
        this.precedent = React.createRef();
        this.state = {
            nom: '',
            prenom: '',
            username: '',
            email: '',
            sexe: '',
            naissance: '',
            lieu: '',
            telephone: '',
            nationalite: '',
            password1: '',
            password2: ''
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    // {/* l'évènement onChange fait appel à la fonction handleChange*/}     

    handleChange(event) {
        const name = event.target.name
        // const type=event.target.type  
        // const value=   type == 'checkbox' ? event.target.checked : event.target.value          
        this.setState({                    //*Modifier la valeur du champ */}
            [name]: event.target.value
        })
    }

    // handleClick = () => {
    //     const navigate = useNavigate()
    //     navigate(-1)
    // }

    handleClick1 = () => {
        console.log("Entrée");
        console.log(this.suivant);
        // this.classe1="toggleForm";
        // this.classe2="";

        this.suivant.current.classList.add('d-none')
        this.precedent.current.classList.remove('d-none');
        if (this.partie2.current.classList.contains('toggleForm')) {
            this.myRef.current.style.transform = 'translateY(-50%)';
            this.myRef.current.style.transition = 'transform .5s';
            this.partie2.current.classList.remove('toggleForm');
            this.partie1.current.classList.add('toggleForm')
        }
    }

    handleClick2 = () => {
        this.precedent.current.classList.add('d-none')
        this.suivant.current.classList.remove('d-none');
        if (this.partie1.current.classList.contains('toggleForm')) {
            this.myRef.current.style.transform = 'translateY(0%)';
            this.myRef.current.style.transition = 'transform .5s';
            this.partie2.current.classList.add('toggleForm')
            this.partie1.current.classList.remove('toggleForm');
        }
    }

    handleClick3 = (e) => {
        const name = e.target.name

        this.setState({
            [name]: e.target.null

        })
        // for (const key in this.setState) {

        //     this.setState[key] = "";
        // }

        
    }


    handleSubmit = (event) => {
        const password1 = event.target.password1
        const password2 = event.target.password2

        if (password1 === password2) {
            alert('Formulaire soumis avec succès')

        } else {
            alert('Les mots de passe ne sont pas identiques!')

        }

        event.preventDefault()
    }




    render() {
        return (
            <div className="back">
                <img src="logo_sansfond.png" alt="logo gnb" className={this.code} />
                <section className="vh-100 " >
                    <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>

                    <div className="container py-5 h-100">
                        <div className="row d-flex justify-content-center align-items-center h-100">
                            <div className="col col-xl-10">
                                <div className="card bords" style={{ overflow: "hidden", }}>
                                    <div className="row g-0" >
                                        <div className="col-md-6 col-lg-5 d-none d-md-block">
                                            <img src="image3.jpg " alt="login form" className="img-fluid image" />

                                        </div>

                                        <div className="col-md-6 col-lg-7 d-flex align-items-center">

                                            <div className="card-body p-4 ps-lg-5 pe-lg-5 pt-lg-3 text-black" id="formContainer" ref={this.myRef}>

                                                <form   >
                                                    <div className="d-flex align-items-center mb-3 pb-1"></div>

                                                    <div id="formPartie1" className="" ref={this.partie1}>

                                                        <Field type='text' name='nom' value={this.state.nom} onChange={this.handleChange} >Nom</Field>

                                                        <Field type='text' name='prenom' value={this.state.prenom} onChange={this.handleChange}>Prénom</Field>

                                                        <Field type="text" name='username' value={this.state.username} onChange={this.handleChange}>Username</Field>

                                                        <FieldRadio name='sexe' value={this.state.sexe} onChange={this.handleChange}>Sexe</FieldRadio>

                                                        <Field type="email" name='emails' value={this.state.email} onChange={this.handleChange}>Email</Field>

                                                        <Field type="tel" name='telephone' value={this.state.telephone} onChange={this.handleChange}>Numéro de téléphone</Field>
                                                    </div>
                                                    {/* <div className="pt-1 mb-4"></div> */}
                                                    <div className="pt-1 mb-4 col-md-2 offset-md-10">
                                                        <div id="suivant" className="btn btn-sm btn-secondary text-white" onClick={this.handleClick1} ref={this.suivant} type="button"><i className="me-2 fa-solid fa-circle-chevron-down "></i>Suivant</div>
                                                        {/* <Link to='/'>Retour<i className="fa-solid fa-circle-chevron-right "></i></Link> */}
                                                    </div>

                                                    <div id="formPartie2" className="toggleForm" ref={this.partie2}>

                                                        <Field type="date" name='naissance' value={this.state.naissance} onChange={this.handleChange}>Date de naissance</Field>

                                                        <Field type="text" name='lieu' value={this.state.lieu} onChange={this.handleChange}>Lieu de naissance</Field>

                                                        <Field type="text" name='nationalite' value={this.state.nationalite} onChange={this.handleChange}>Nationalité</Field>

                                                        <Field type="password" name='password1' value={this.state.password1} onChange={this.handleChange}>Mot de passe</Field>

                                                        <Field type="password" name='password2' value={this.state.password2} onChange={this.handleChange}>Confirmer le mot de passe</Field>
                                                        <div className="row  d-none" ref={this.precedent}>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="precedent" className="btn btn-sm btn-secondary text-white" onClick={this.handleClick2}><i className="me-2 fa-solid fa-circle-chevron-up "></i>Précédent</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4">
                                                                <div id="annuler" className="btn btn-sm btn-danger text-white" onClick={this.handleClick3}><i className="me-2 fa-solid fa-circle-xmark "></i>Annuler</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="inscrire" className="btn btn-sm btn-primary text-white"><i className="me-2 fa-solid fa-circle-user"></i>S'inscrire</div>
                                                            </div>
                                                        </div>

                                                    </div>

                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}
