import React from "react";
import './inscription.css';
import { useNavigate } from "react-router-dom";
// import { type } from "@testing-library/user-event/dist/type";

function Fieldtext(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='text' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fieldmail(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='email' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fielddate(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='date' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fieldnumber(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='tel' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

function Fieldpass(props){
    const {name,value,onChange,children}=props
    
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name} >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <input type='password' name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg"/>       
        </div>
    )
}

// Créer le composant Inscription
export default class Inscription extends React.Component{
 //Créer un constructeur qui prendra en paramètre les propriétés
    constructor(props){
        super(props)
        this.state={
            nom:'',
            prenom:'',
            username:'',
            email:'',
            sexe:'',
            naissance:'',
            lieu:'',
            telephone:'',
            nationalite:'',
            password1:'',
            password2:''
        }
this.handleChange=this.handleChange.bind(this)
this.handleSubmit=this.handleSubmit.bind(this)
    }

// {/* l'évènement onChange fait appel à la fonction handleChange*/}     

handleChange(event){   
    const name=event.target.name
    // const type=event.target.type  
    // const value=   type == 'checkbox' ? event.target.checked : event.target.value          
        this.setState({                    //*Modifier la valeur du champ */}
            [name]: event.target.value
        })
    }

 handleClick = () =>{
        const navigate = useNavigate()
          navigate(-1)
    }

 handleSubmit = (event) =>{
    const password1=event.target.password1
    const password2=event.target.password2

    if(password1!==password2){
        alert('Les mots de passe ne sont pas identiques!')
    }else{
        alert('Formulaire soumis avec succès')
    }
    event.preventDefault()
 }   

// verification = (event) =>{
//     const password1=event.target.password1
//     const password2=event.target.password2
//     // const password2=this.state.password2.slice();
//     if (password1===password2) {
//         return(alert('inscription réussi'))
//     } else {
//         return(alert('les mots de passes ne sont pas identiques'))
//     }
// }

    render(){
        return(
            <div className="back">
            <img src="logo_sansfond.png" alt="logo gnb" className="text-center  taille_image"/> 
            <section className="vh-100 " >
            <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>

                <div className="container py-5 h-100">
                    <div className="row d-flex justify-content-center align-items-center h-100">
                        <div className="col col-xl-10">
                            <div className="card bord" >
                                <div className="row g-0">
                                    <div className="col-md-6 col-lg-5 d-none d-md-block">
                                        <img src="image3.jpg"
                                        alt="login form" className="img-fluid image"  />

                                    </div>
                                    
                                    <div className="col-md-6 col-lg-7 d-flex align-items-center">
                                        <div className="card-body p-4 p-lg-5 text-black">
            
                                            <form onSubmit={this.handleSubmit}>

                                                <div className="d-flex align-items-center mb-3 pb-1">
                                                    {/* <span className="h1 fw-bold mb-0">Global Network Business</span> */}
                                                </div>


                                                <Fieldtext name='nom' value={this.state.nom} onChange={this.handleChange} pla>Nom</Fieldtext>
                                                <Fieldtext name='prenom' value={this.state.prenom} onChange={this.handleChange}>Prénom</Fieldtext>
                                                <Fieldtext name='username' value={this.state.username} onChange={this.handleChange}>Pseudo</Fieldtext>
                                                <Fieldmail name='email' value={this.state.email} onChange={this.handleChange}>Email</Fieldmail>
                                                <Fieldtext name='sexe' value={this.state.sexe} onChange={this.handleChange}>Sexe</Fieldtext>
                                                <Fielddate name='prenom' value={this.state.prenom} onChange={this.handleChange}>Prénom</Fielddate>
                                                <Fieldtext name='lieu' value={this.state.lieu} onChange={this.handleChange}>Lieu de naissance</Fieldtext>
                                                <Fieldnumber name='telephone' value={this.state.telephone} onChange={this.handleChange}>Numéro de téléphone</Fieldnumber>
                                                <Fieldtext name='nationalite' value={this.state.nationalite} onChange={this.handleChange}>Nationalité</Fieldtext>

                                                 {/* <div className="form-outline mb-4">
                                                    <label className="form-label" >Nom</label>
                                                    <input type="text"  className="form-control form-control-lg espace" id="nom" name="nom" value={this.state.nom} onChange={this.handleChange} placeholder='entrez votre nom'required/>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Prénom</label>
                                                    <input type="text"  className="form-control form-control-lg espace" id="prenom" name="prenom" value={this.state.prenom} onChange={this.handleChange} placeholder='entrez votre/vos prenom(s)' required/>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Nom d'utilisateur</label>
                                                    <input type="text"  className="form-control form-control-lg espace" id="username" name="username" value={this.state.username} onChange={this.handleChange} placeholder="entrez un nom d'utilisateur" required/>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Email</label>
                                                    <input type="email"  className="form-control form-control-lg espace" id="email" name="email" value={this.state.email} onChange={this.handleChange} placeholder="xxxx@gmail.com" required/>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label">Sexe</label>
                                                    <select name="sexe" value={this.state.sexe} onChange={this.handleChange} required>
                                                        <option value='homme'>Homme</option>
                                                        <option value='femme'>Femme</option>
                                                    </select>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Date de naissance</label>
                                                    <input type="date"  className="form-control form-control-lg espace" id=" naissance" name=" naissance" value={this.state.naissance} onChange={this.handleChange} placeholder="entrez votre date de naissance" />
                                                </div>
                                               
                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Lieu de naissance</label>
                                                    <input type="text"  className="form-control form-control-lg espace" id="lieu" name="lieu" value={this.state.lieu} onChange={this.handleChange} placeholder="entrez votre lieu de naissance" required/>
                                                </div>

                                                <div className="form-outline mb-4">
                                                    <label className="form-label" >Numéro de téléphone</label>
                                                    <input type="tel"  className="form-control form-control-lg espace" id="telephone" name="telephone" value={this.state.telephone} onChange={this.handleChange} placeholder="entrez votre contact téléphonique" required/>
                                                </div>

