import React from 'react';
 import { useFormik } from 'formik';
 import './inscription.css';

 function Field(props) {
    const{type,name,value,onChange,onBlur,children} =props
    return(
        <div className="form-outline mb-4">
            <label htmlFor={name}>{children}</label>
         {/* l'évènement onChange pour gérer les changement dans les inputs  */}
            <input type={type} name={name} value={value} onChange={onChange} onBlur={onBlur} id={name} className="form-control form-control-lg" />
            
        </div>    
    )
}

function FieldRadio(props) {
    const { name, value, onChange, children } = props

    return (
        <div className="form-outline mb-4">

            <label  >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <div>
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio1" className="form-check-input" />
                    <label className="custom-control-label" for="radio1">Masculin</label>
                </div>
            </div>
            <div>
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio2" className="form-check-input" />
                    <label className="custom-control-label" for="radio2">Feminin</label>
                </div>
            </div>
        </div>
    )
}
 
 const validate = values => {
   const errors = {};
 
   if (!values.nom) {
     errors.nom = 'Required';
   } else if (values.nom.length > 15) {
     errors.nom = 'Le nom doit être 15 caractères ou moins ';
   }
 
   if (!values.prenom) {
     errors.lastName = 'Required';
   } else if (values.prenom.length > 20) {
     errors.prenom = 'Le prenom doit être 20 caractères ou moins';
   }

   if (!values.username) {
    errors.username = 'Required';
  } else if (values.username.length > 10) {
    errors.username = 'Le prenom doit être 10 caractères ou moins';
  }
 
   if (!values.email) {
     errors.email = 'Required';
   } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
     errors.email = 'Adresse email invalide';
   }

   if(!values.sexe){
    errors.sexe = 'Required';
   }

   if(!values.naissance){
    errors.naissance='Required';
   }
   
   if (!values.lieu) {
    errors.naissance = 'Required';
  } else if (values.naissance.length > 20) {
    errors.naissance = 'Le prenom doit être 20 caractères ou moins';
  }
  if (!values.nationalite) {
    errors.nationalite = 'Required';
  } else if (values.nationalite.length > 10) {
    errors.nationalite = 'Le prenom doit être 10 caractères ou moins';
  }

  const passwordRegex = /(?=.*[0-9])/;
  if(!values.password){
    errors.password='Required';
  }else if (values.length < 8) {
    errors.password= "*Password must be 8 characters long.";
  } else if (!passwordRegex.test(values)) {
    errors.password = "*Invalid password. Must contain one number.";
  }



   return errors;
 };
 
 function SignupForm ()  {
   const formik = useFormik({
     initialValues: {
       nom: '',
       username: '',
       email:'',
       sexe: '',
       naissance: '',
       lieu: '',
       telephone: '',
       nationalite: '',
       password: '',
       confirmPassword:'' 
     },
     validate,
     onSubmit: values => {
       alert(JSON.stringify(values, null, 2));
     },
   });
   return (
    <div className="back">
    <img src="logo_sansfond.png" alt="logo gnb" className="text-center  taille_image" />
    <section className="vh-100 " >
        <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>
    
        <div className="container py-5 h-100">
            <div className="row d-flex justify-content-center align-items-center h-100">
                <div className="col col-xl-10">
                    <div className="card bords" >
                        <div className="row g-0">
                            <div className="col-md-6 col-lg-5 d-none d-md-block">
                                <img src="image3.jpg " alt="login form" className="img-fluid image" />
    
                            </div>
    
                            <div className="col-md-6 col-lg-7 d-flex align-items-center">
                                
                                <div className="card-body p-4 p-lg-5 text-black" id="formContainer" >
    
                                    <form onSubmit={formik.handleSubmit} >
                                        <div className="d-flex align-items-center mb-3 pb-1"></div>
                                        
                                        <div  id="formPartie1" className="" >
    
                                            <Field type='text' name='nom' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.nom}>Nom</Field>
                                            {formik.touched.nom && formik.errors.nom ? (<div className='erreur'>{formik.errors.nom}</div>): null}
    
                                            <Field type='text' name='prenom' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.prenom}>Prénom</Field>
                                            {formik.touched.prenom && formik.errors.prenom ? (<div className='erreur'>{formik.errors.prenom}</div>): null}
    
                                            <Field type="text" name='username' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.username}>Username</Field>
                                            {formik.touched.username && formik.errors.username ? (<div className='erreur'>{formik.errors.username}</div>): null}
    
                                            <Field type="email" name='email' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.email}>Email</Field>
                                            {formik.touched.email && formik.errors.email ? (<div className='erreur'>{formik.errors.email}</div>): null}
    
                                            <Field type="tel" name='telephone' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.telephone}>Numéro de téléphone</Field>
                                            {formik.touched.telephone && formik.errors.telephone ? (<div>{formik.errors.telephone}</div>): null}
    
                                        </div>
                                        {/* <div className="pt-1 mb-4"></div> */}
                                        <div className="pt-1 mb-4 col-md-2 offset-md-10">
                                            <button id="suivant"  className="btn btn-sm btn-secondary text-white active"  >Suivant<i className="fa-solid fa-circle-chevron-right "></i></button>
                                            {/* <Link to='/'>Retour<i className="fa-solid fa-circle-chevron-right "></i></Link> */}
                                        </div>
                                       
                                    
                                    <div id="formPartie2" className="" >
                                        <div className="d-flex align-items-center mb-3 pb-1"></div>
                                        
                                            <Field type="date" name='naissance' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.naissance}>Date de naissance</Field>
                                            {formik.touched.naissance && formik.errors.naissance ? (<div>{formik.errors.naissance}</div>): null}
    
                                            <Field type="text" name='lieu' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.lieu}>Lieu de naissance</Field>
                                            {formik.touched.lieu&& formik.errors.lieu? (<div>{formik.errors.lieu}</div>): null}
    
                                            <FieldRadio name='sexe' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.sexe}>Sexe</FieldRadio>
                                            {formik.touched.sexe && formik.errors.sexe ? (<div>{formik.errors.sexe}</div>): null}
    
                                            <Field type="text" name='nationalite' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.nationalite}>Nationalité</Field>
                                            {formik.touched.nationalite && formik.errors.nationalite ? (<div>{formik.errors.nationalite}</div>): null}
    
                                            <Field type="password" name='password' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.password}>Mot de passe</Field>
                                            {formik.touched.password && formik.errors.password ? (<div>{formik.errors.password}</div>): null}
    
                                            <Field type="password" name='confirmPassword' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.confirmPassword}>Confirmer le mot de passe</Field>
                                            {formik.touched.confirmPassword && formik.errors.confirmPassword ? (<div>{formik.errors.confirmPassword}</div>): null}
    
                                            <div className="pt-1 mb-4 col-md-2">
                                            <button id="precedent"  className="btn btn-sm btn-secondary text-white "  >Précédent<i className="fa-solid fa-circle-chevron-left "></i></button>
                                        </div>
                                       <div className=" offset-md-8"></div>
                                        <div className="pt-1 mb-4 col-md-2">
                                            <button className="btn btn-sm btn-secondary text-white boutons" type='submit' >S'inscrire</button>
                                        </div>
                                    
    
                                    </div>
                                        
    
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </section>
    </div>
   )
 }

 export default SignupForm
