import { useFormik } from "formik";
import React, { useRef, useState } from "react";
import './inscription.css';
// import {useEffect} from 'react';
// import { useNavigate } from "react-router-dom";

function FieldRadio(props) {
    const { name, value, onChange, children } = props

    return (
        <div className="form-outline mb-4 ">

            <label  >{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs                  */}
            <div >
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio1" className="form-check-input" />
                    <label className="custom-control-label" for="radio1">Masculin</label>
                </div>
                <div className="form-check form-check-inline">
                    <input type="radio" name={name} value={value} onChange={onChange} id="radio2" className="form-check-input" />
                    <label className="custom-control-label" for="radio2">Feminin</label>
                </div>
            </div>
        </div>
    )
}

function Field(props) {
    const { type, name, value, onChange, children } = props
    return (
        <div className="form-outline mb-4">
            <label htmlFor={name}>{children}</label>
            {/* l'évènement onChange pour gérer les changement dans les inputs  */}
            <input type={type} name={name} value={value} onChange={onChange} id={name} className="form-control form-control-lg" />

        </div>
    )
}

const validate = values => {
    const errors = {};
  
    if (!values.nom) {
      errors.nom = 'Required';
    } else if (values.nom.length > 15) {
      errors.nom = 'Le nom doit être 15 caractères ou moins ';
    }
  
    if (!values.prenom) {
      errors.lastName = 'Required';
    } else if (values.prenom.length > 20) {
      errors.prenom = 'Le prenom doit être 20 caractères ou moins';
    }
 
    if (!values.username) {
     errors.username = 'Required';
   } else if (values.username.length > 10) {
     errors.username = 'Le prenom doit être 10 caractères ou moins';
   }
  
    if (!values.email) {
      errors.email = 'Required';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Adresse email invalide';
    }
 
    if(!values.sexe){
     errors.sexe = 'Required';
    }
 
    if(!values.naissance){
     errors.naissance='Required';
    }
    
    if (!values.lieu) {
     errors.naissance = 'Required';
   } else if (values.naissance.length > 20) {
     errors.naissance = 'Le prenom doit être 20 caractères ou moins';
   }
   if (!values.nationalite) {
     errors.nationalite = 'Required';
   } else if (values.nationalite.length > 10) {
     errors.nationalite = 'Le prenom doit être 10 caractères ou moins';
   }
 
   const passwordRegex = /(?=.*[0-9])/;
   if(!values.password1){
     errors.password1='Required';
   }else if (values.length < 8) {
     errors.password1= "*Le mot de passe doit contenir au moins 8 caractères.";
   } else if (!passwordRegex.test(values)) {
     errors.password1 = "Mot de passe invalide.Doit contenir au moins un chiffre";
   }
 
 
 
    return errors;
  };
  

// {/* // Créer le composant Inscription */}

function Modification(){ 
const [nom, setNom]=useState('');
const [prenom, setPrenom]=useState('');
const [username, setUsername]=useState('');
const [email, setEmail]=useState('');
const [sexe, setSexe]=useState('');
const [naissance, setNaissance]=useState('');
const [lieu, setLieu]=useState('');
const [telephone, setTelephone]=useState('');
const [nationalite, setNationalite]=useState('');
const [password1, setPassword1]=useState('');
const [password2, setPassword2]=useState('');

// const handleChangeNom = (e) =>{
//     setNom(e.target.value);
// }
// const handleChangePrenom = (e) =>{
//     setPrenom(e.target.value);
// }
// const handleChangeUsername = (e) =>{
//     setUsername(e.target.value);
// }
// const handleChangeEmail = (e) =>{
//     setEmail(e.target.value);
// }
// const handleChangeSexe = (e) =>{
//     setSexe(e.target.value);
// }
// const handleChangeNaissance = (e) =>{
//     setNaissance(e.target.value);
// }
// const handleChangeLieu = (e) =>{
//     setLieu(e.target.value);
// }
// const handleChangeTelephone = (e) =>{
//     setTelephone(e.target.value);
// }
// const handleChangeNationalite = (e) =>{
//     setNationalite(e.target.value);
// }
// const handleChangePassword = (e) =>{
//     setPassword1(e.target.value);
// }
// const handleChangeConfirmPassword = (e) =>{
//     setPassword2(e.target.value);
// }

    const code=useRef(null);
    const myRef=useRef(null);
    const partie1=useRef(null);
    const partie2=useRef(null);
    const suivant=useRef(null);
    const precedent=useRef(null);

    code.current="text-center  taille_image";

   const handleClick1 = () => {
        suivant.current.classList.add('d-none')
        precedent.current.classList.remove('d-none');
        if (partie2.current.classList.contains('toggleForm')) {
            myRef.current.style.transform = 'translateY(-50%)';
            myRef.current.style.transition = 'transform .5s';
            partie2.current.classList.remove('toggleForm');
            partie1.current.classList.add('toggleForm')
        }
    }

  const  handleClick2 = () => {
        precedent.current.classList.add('d-none')
        suivant.current.classList.remove('d-none');
        if (partie1.current.classList.contains('toggleForm')) {
            myRef.current.style.transform = 'translateY(0%)';
            myRef.current.style.transition = 'transform .5s';
            partie2.current.classList.add('toggleForm')
            partie1.current.classList.remove('toggleForm');
        }
    }

 const handleClick3 = () => {
    const [nom, setNom]=useState('');
    const [prenom, setPrenom]=useState('');
    const [username, setUsername]=useState('');
    const [emails, setEmails]=useState('');
    const [sexe, setSexe]=useState('');
    const [naissance, setNaissance]=useState('');
    const [lieu, setLieu]=useState('');
    const [telephone, setTelephone]=useState('');
    const [nationalite, setNationalite]=useState('');
    const [password1, setPassword1]=useState('');
    const [password2, setPassword2]=useState('');
    
       precedent.current.classList.add('d-none')
       suivant.current.classList.remove('d-none');
        if (partie1.current.classList.contains('toggleForm')) {
           myRef.current.style.transform = 'translateY(0%)';
           myRef.current.style.transition = 'transform .5s';
           partie2.current.classList.add('toggleForm')
           partie1.current.classList.remove('toggleForm');
        }

        
    }

    const formik=useFormik({
        initialValues: {
            nom: '',
            username: '',
            email:'',
            sexe: '',
            naissance: '',
            lieu: '',
            telephone: '',
            nationalite: '',
            password1: '',
            password2:'' ,},
            validate,
            onSubmit: values => {
              alert(JSON.stringify(values, null, 2));
            },
    })
        return (
            <div className="back">
                <img src="logo_sansfond.png" alt="logo gnb" ref={code} />
                <section className="vh-100 " >
                    <h1 className=" text-center  fw-normal mb-3 pb-3 space" >Inscrivez-vous</h1>

                    <div className="container py-5 h-100">
                        <div className="row d-flex justify-content-center align-items-center h-100">
                            <div className="col col-xl-10">
                                <div className="card bords" style={{ overflow: "hidden", }}>
                                    <div className="row g-0" >
                                        <div className="col-md-6 col-lg-5 d-none d-md-block">
                                            <img src="image3.jpg " alt="login form" className="img-fluid image" />

                                        </div>

                                        <div className="col-md-6 col-lg-7 d-flex align-items-center">

                                            <div className="card-body p-4 ps-lg-5 pe-lg-5 pt-lg-3 text-black" id="formContainer" ref={myRef}>

                                                <form  onSubmit={formik.handleSubmit} >
                                                    <div className="d-flex align-items-center mb-3 pb-1"></div>

                                                    <div id="formPartie1" className="" ref={partie1}>

                                                        <Field type='text' name='nom' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.nom}>Nom</Field>
                                                        {formik.touched.nom && formik.errors.nom ? (<div className='erreur'>{formik.errors.nom}</div>): null}
        
                                                        <Field type='text' name='prenom' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.prenom}>Prénom</Field>
                                                        {formik.touched.prenom && formik.errors.prenom ? (<div className='erreur'>{formik.errors.prenom}</div>): null}
        
                                                        <Field type="text" name='username' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.username}>Username</Field>
                                                        {formik.touched.username && formik.errors.username ? (<div className='erreur'>{formik.errors.username}</div>): null}
                
                                                        <Field type="email" name='email' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.email}>Email</Field>
                                                        {formik.touched.email && formik.errors.email ? (<div className='erreur'>{formik.errors.email}</div>): null}
                
                                                        <Field type="tel" name='telephone' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.telephone}>Numéro de téléphone</Field>
                                                        {formik.touched.telephone && formik.errors.telephone ? (<div className='erreur'>{formik.errors.telephone}</div>): null}
                
                                                    </div>
                                                    {/* <div className="pt-1 mb-4"></div> */}
                                                    <div className="pt-1 mb-4 col-md-2 offset-md-10">
                                                        <div id="suivant" className="btn btn-sm btn-secondary text-white" onClick={this.handleClick1} ref={suivant} type="button"><i className="me-2 fa-solid fa-circle-chevron-down "></i>Suivant</div>
                                                        {/* <Link to='/'>Retour<i className="fa-solid fa-circle-chevron-right "></i></Link> */}
                                                    </div>

                                                    <div id="formPartie2" className="toggleForm" ref={this.partie2}>

                                                        <Field type="date" name='naissance' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.naissance}>Date de naissance</Field>
                                                        {formik.touched.naissance && formik.errors.naissance ? (<div className='erreur'>{formik.errors.naissance}</div>): null}
                
                                                        <Field type="text" name='lieu' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.lieu}>Lieu de naissance</Field>
                                                        {formik.touched.lieu&& formik.errors.lieu? (<div className='erreur'>{formik.errors.lieu}</div>): null}
                
                                                        <FieldRadio name='sexe' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.sexe}>Sexe</FieldRadio>
                                                        {formik.touched.sexe && formik.errors.sexe ? (<div className='erreur'>{formik.errors.sexe}</div>): null}
                
                                                        <Field type="text" name='nationalite' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.nationalite}>Nationalité</Field>
                                                        {formik.touched.nationalite && formik.errors.nationalite ? (<div className='erreur'>{formik.errors.nationalite}</div>): null}
                
                                                        <Field type="password" name='password' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.password}>Mot de passe</Field>
                                                        {formik.touched.password && formik.errors.password ? (<div className='erreur'>{formik.errors.password}</div>): null}
                
                                                        <Field type="password" name='confirmPassword' onChange={formik.handleChange} onBlur={formik.handleBlur} value={formik.values.confirmPassword}>Confirmer le mot de passe</Field>
                                                        {formik.touched.confirmPassword && formik.errors.confirmPassword ? (<div className='erreur'>{formik.errors.confirmPassword}</div>): null}
                                                                
                                                        <div className="row  d-none" ref={this.precedent}>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="precedent" className="btn btn-sm btn-secondary text-white" onClick={this.handleClick2}><i className="me-2 fa-solid fa-circle-chevron-up "></i>Précédent</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4">
                                                                <div id="annuler" className="btn btn-sm btn-danger text-white" onClick={this.handleClick3}><i className="me-2 fa-solid fa-circle-xmark "></i>Annuler</div>
                                                            </div>
                                                            <div className="pt-1 mb-4 col-md-4 ">
                                                                <div id="inscrire" className="btn btn-sm btn-primary text-white"><i className="me-2 fa-solid fa-circle-user"></i>S'inscrire</div>
                                                            </div>
                                                        </div>

                                                    </div>

                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
}

export default Modification
